public class ShorthandOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


	int number=10;
	number+=20;
	System.out.println(number);
	number+=20+30;
	System.out.println(number);
	number+=number+50;
	System.out.println(number);
	
	
	
	
	
	
	
	
	
	}

}
